SELECT
count(*) AS num,diMingBeiZhu
FROM

(
SELECT
*
FROM

    (
    SELECT
    *
    FROM
    (
    SELECT
    e.emnumber
    FROM
    emdata e


            e.luMianLev = #{roadLevel}


            AND e.Tianqicondition = #{weather}


            AND e.workPlaceRel = #{workPlaceRel}


            AND e.Jcktype = #{intersectionType}


    ) eResult INNER JOIN



    (
    SELECT
    *
    FROM
    accidencecollectiondata


            team_name = #{teamName}


            AND riqi  >=  #{startTime}


            AND riqi  <=  #{endTime}


    ) acd




    ON eResult.emnumber = acd.environmentnumber
    ) acdResult




    INNER JOIN


(
SELECT
accidencnumber,
diMingBeiZhu
FROM
accidentdata


        accidentdata.carCollisionType = #{carCollisionType}


        AND accidentdata.taoYi = #{troEscape}


        AND accidentdata.xianQu = #{areaName}


        AND accidentdata.is_workday = #{isWorkDay}


) aResult


    ON acdResult.shigunumber = aResult.accidencnumber
    ) res1



    INNER JOIN (
    SELECT
    carnumber
    FROM
    cardata


            Leixing = #{carType}


    ) carResult ON res1.carnumber = carResult.carnumber

GROUP BY diMingBeiZhu

SELECT
count(*) as num,diMingBeiZhu,yanZhongCd as keyRes
FROM

(
SELECT
*
FROM

    (
    SELECT
    *
    FROM
    (
    SELECT
    e.emnumber
    FROM
    emdata e


            e.luMianLev = #{roadLevel}


            AND e.Tianqicondition = #{weather}


            AND e.workPlaceRel = #{workPlaceRel}


            AND e.Jcktype = #{intersectionType}


    ) eResult INNER JOIN



    (
    SELECT
    *
    FROM
    accidencecollectiondata


            team_name = #{teamName}


            AND riqi  >=  #{startTime}


            AND riqi  <=  #{endTime}


    ) acd




    ON eResult.emnumber = acd.environmentnumber
    ) acdResult




    INNER JOIN


(
SELECT
accidencnumber,
diMingBeiZhu,
yanZhongCd
FROM
accidentdata


        accidentdata.carCollisionType = #{carCollisionType}


        AND accidentdata.taoYi = #{troEscape}


        AND accidentdata.xianQu = #{areaName}


        AND accidentdata.is_workday = #{isWorkDay}


) aResult


    ON acdResult.shigunumber = aResult.accidencnumber
    ) res1



    INNER JOIN (
    SELECT
    carnumber
    FROM
    cardata


            Leixing = #{carType}


    ) carResult ON res1.carnumber = carResult.carnumber

GROUP BY diMingBeiZhu,yanZhongCd

SELECT count(*) as num,diMingBeiZhu
,yanZhongCd as keyRes
,YEAR (riqi) AS yearRes
,MONTH (riqi) as monthRes
,DAY(riqi) as dayRes
FROM
( SELECT * FROM
( SELECT * FROM ( SELECT e.emnumber FROM emdata e
e.luMianLev = #{roadLevel}
AND e.Tianqicondition = #{weather}
AND e.workPlaceRel = #{workPlaceRel}
AND e.Jcktype = #{intersectionType}
) eResult INNER JOIN
( SELECT * FROM accidencecollectiondata
team_name = #{teamName}
AND riqi >= #{startTime}
AND riqi <= #{endTime}
) acd
ON eResult.emnumber = acd.environmentnumber ) acdResult
INNER JOIN
( SELECT accidencnumber, diMingBeiZhu, yanZhongCd FROM accidentdata
accidentdata.yanZhongCd = '仅财产损失'
OR accidentdata.yanZhongCd = '轻伤'
OR accidentdata.yanZhongCd = '重伤'
OR accidentdata.yanZhongCd = '死亡'
and accidentdata.carCollisionType = #{carCollisionType}
AND accidentdata.taoYi = #{troEscape}
AND accidentdata.xianQu = #{areaName}
AND accidentdata.is_workday = #{isWorkDay}
) aResult
ON acdResult.shigunumber = aResult.accidencnumber ) res1
INNER JOIN ( SELECT carnumber FROM cardata
Leixing = #{carType}
) carResult ON res1.carnumber = carResult.carnumber
GROUP BY diMingBeiZhu
,yanZhongCd
,YEAR (riqi)
,MONTH (riqi)
,DAY(riqi)

SELECT
count(*) AS num
FROM
(

SELECT
*
FROM
(

SELECT
*
FROM
accidencecollectiondata


        team_name = #{teamName}


        AND riqi  >=  #{startTime}


        AND riqi  <=  #{endTime}


) acdResult


    INNER JOIN (
    SELECT
    accidencnumber
    FROM
    accidentdata
    WHERE

        accidentdata.xianQu = #{areaName}


        diMingBeiZhu = #{roadType}

    ) aResult ON acdResult.shigunumber = aResult.accidencnumber


) res1

SELECT count(*) AS num, yanZhongCd as keyRes FROM (
SELECT * FROM (
SELECT * FROM accidencecollectiondata
team_name = #{teamName}
AND riqi >= #{startTime}
AND riqi <= #{endTime}
) acdResult
INNER JOIN ( SELECT accidencnumber, yanZhongCd FROM accidentdata
accidentdata.xianQu = #{areaName}
diMingBeiZhu = #{roadType}
) aResult ON acdResult.shigunumber = aResult.accidencnumber
) res1
GROUP BY yanZhongCd

SELECT count(*) AS num, shigu as keyRes FROM (
SELECT * FROM (
SELECT * FROM accidencecollectiondata
team_name = #{teamName}
AND riqi >= #{startTime}
AND riqi <= #{endTime}
) acdResult
INNER JOIN ( SELECT accidencnumber, shigu FROM accidentdata
accidentdata.xianQu = #{areaName}
diMingBeiZhu = #{roadType}
) aResult ON acdResult.shigunumber = aResult.accidencnumber
) res1
GROUP BY shigu

SELECT count(*) AS num, Tianqicondition as keyRes FROM (
SELECT * FROM ( SELECT * FROM ( SELECT e.emnumber, e.Tianqicondition FROM emdata e ) eResult INNER JOIN ( SELECT * FROM accidencecollectiondata
team_name = #{teamName}
AND riqi >= #{startTime}
AND riqi <= #{endTime}
) acd ON eResult.emnumber = acd.environmentnumber ) acdResult
INNER JOIN ( SELECT accidencnumber FROM accidentdata WHERE
accidentdata.xianQu = #{areaName}
diMingBeiZhu = #{roadType}
) aResult ON acdResult.shigunumber = aResult.accidencnumber
) res1
GROUP BY Tianqicondition

SELECT count(*) AS num, Leixing as keyRes FROM (
SELECT * FROM (
SELECT * FROM accidencecollectiondata
team_name = #{teamName}
AND riqi >= #{startTime}
AND riqi <= #{endTime}
) acdResult
INNER JOIN ( SELECT accidencnumber FROM accidentdata WHERE
accidentdata.xianQu = #{areaName}
diMingBeiZhu = #{roadType}
) aResult ON acdResult.shigunumber = aResult.accidencnumber
) res1
INNER JOIN ( SELECT carnumber, Leixing FROM cardata ) carResult ON res1.carnumber = carResult.carnumber
GROUP BY Leixing
